function digCalculationR(arr) {
    let maxProduct = 0;
    let k = 0;
    let trueNumbers = [];
    let inRow = 0;

    for (let row = 0; row < lengthMatrix - 3; row++) {
        let maxProductRow = 0;

        for (let cols = lengthMatrix - 4; cols > 0; cols--) {
            let sum = 1;
            let numbers = [];
            k = 0;

            for (let i = 0; i < 4; i++) {
                k = row + i;

                //записуємо числа, які ми обчислюєм в масив
                numbers[i] = arr[k][cols + k];
                sum = arr[k][cols + k] * sum;
            }

            if (isNaN(sum) == true) {
                sum = 0;
            }

                // console.log("numbers: " + numbers);
                maxProductRow = Math.max(maxProductRow, sum); // максимальний добуток чисел в рядку кожному рядку

                // знаходимо числа, які дали максимальний добуток
                if (maxProduct < sum) {
                    trueNumbers = numbers;
                    inRow = cols;
                }

                maxProduct = Math.max(maxProduct, sum); // Максимальний добуток з усіх рядків

                // console.log(`(${row}): ${sum}`);
        }
        // console.log(`(${row}): max: ${maxProductRow}`)
    }
    console.log(`${inRow}: numbers: ${trueNumbers}`);
    console.log(`max diagonal Right number: ${maxProduct}`);
    return maxProduct;
}


function digCalculationL(arr) {
    let maxProduct = 0;
    let k = 0;
    let trueNumbers = [];
    let inRow = 0;

    for (let row = 0; row < lengthMatrix - 3; row++) {
        let maxProductRow = 0;

        for (let cols = 3; cols < lengthMatrix; cols++) {
            let sum = 1;
            let numbers = [];
            k = 0;

            for (let i = 0; i < 4; i++) {
                k = row + i;

                //записуємо числа, які ми обчислюєм в масив
                numbers[i] = arr[k][cols - k];
                sum = arr[k][cols - k] * sum;
            }

            if (isNaN(sum) == true) {
                sum = 0;
            }

                // console.log("numbers: " + numbers);
                maxProductRow = Math.max(maxProductRow, sum); // максимальний добуток чисел в рядку кожному рядку

                // знаходимо числа, які дали максимальний добуток
                if (maxProduct < sum) {
                    trueNumbers = numbers;
                    inRow = cols;
                }

                maxProduct = Math.max(maxProduct, sum); // Максимальний добуток з усіх рядків

                // console.log(`(${row}): ${sum}`);
        }
        // console.log(`(${row}): max: ${maxProductRow}`)
    }
    console.log(`${inRow}: numbers: ${trueNumbers}`);
    console.log(`max diagonal Left number: ${maxProduct}`);
    return maxProduct;
}


// Максимальний добуток стовпчиків
function colsCalculation(arr) {
    let maxProduct = 0;
    let k = 0;
    let trueNumbers = [];
    let inCols = 0;

    for (let cols = 0; cols < lengthMatrix; cols++) {
        let maxProductRow = 0;

        for (let rows = 0; rows < lengthMatrix - 3; rows++) {
            let sum = 1;
            let numbers = [];
            k = 0;

            for (let i = 0; i < 4; i++) {
                k = rows + i;

                //записуємо числа, які ми обчислюєм в масив
                sum = arr[k][cols] * sum;
                numbers[i] = arr[k][cols];
            }
            // console.log("numbers: " + numbers);
            maxProductRow = Math.max(maxProductRow, sum); // максимальний добуток чисел в кожному стопчику

            // знаходимо числа, які дали максимальний добуток
            if (maxProduct < sum) {
                trueNumbers = numbers;
                inCols = cols;
            }

            maxProduct = Math.max(maxProduct, sum); // Максимальний добуток з усіх стопчиків

            // console.log(`(${row}): ${sum}`);
        }
        // console.log(`(${row}): max: ${maxProductRow}`)
    }
    console.log(`(cols: ${inCols}): numbers: ${trueNumbers}`);
    console.log(`max cols number: ${maxProduct}`);
    return maxProduct;
}


// Максимальний добуток рядків
function rowCalculation(arr) {
    let maxProduct = 0;
    let k = 0;
    let trueNumbers = [];
    let inRow = 0;

    for (let row = 0; row < lengthMatrix; row++) {
        let maxProductRow = 0;

        for (let cols = 0; cols < lengthMatrix - 3; cols++) {
            let sum = 1;
            let numbers = [];
            k = 0;

            for (let i = 0; i < 4; i++) {
                k = cols + i;

                //записуємо числа, які ми обчислюєм в масив
                sum = arr[row][k] * sum;
                numbers[i] = arr[row][k];
            }
            // console.log("numbers: " + numbers);
            maxProductRow = Math.max(maxProductRow, sum); // максимальний добуток чисел в рядку кожному рядку

            // знаходимо числа, які дали максимальний добуток
            if (maxProduct < sum) {
                trueNumbers = numbers;
                inRow = row;
            }

            maxProduct = Math.max(maxProduct, sum); // Максимальний добуток з усіх рядків

            // console.log(`(${row}): ${sum}`);
        }
        // console.log(`(${row}): max: ${maxProductRow}`)
    }
    console.log(`(row: ${inRow}): numbers: ${trueNumbers}`);
    console.log(`max row number: ${maxProduct}`);
    return maxProduct;
}

// Створення масиву матриці
function createMatrix(str) {
    let number;
    let matrix = [];
    str = delSpace(str);
    
    // Виділяєм кількість стовпців в у кожному рядку
    for (let i = 0; i < lengthMatrix; i++) {
        matrix[i] = new Array(); //створюємо масив
        matrix[i].length = lengthMatrix; // довжина кожного рядка
    }

    for (let cols = 0; cols < lengthMatrix; cols++) {
        for (let rows = 0; rows < lengthMatrix; rows++) {
            number = +str.substring(0, 2); // беремо кожні два перші числа + переводимо строку в число
            matrix[cols][rows] = number; // записуєм по координатам в матрицю кожне взяте число
            str = str.substring(2); // видаляєм записані 2 числа зі строки
        }
    }

    return matrix;
}

// Видалення усіх пробілів зі строки
function delSpace(str) {
    let newStr = '';

    for (let i = 0; i < str.length; i++) {

        // створюємо строку з чисел без пробілів
        if (str[i] != ' ') {
            newStr = newStr + str[i];
        }
    }

    return newStr;
}
